    const bgChange = () => {   
let newBg = 'yellow';
setbg(newBg);
    };
    return(
        <>
            <div style={{backgroundColor:bg}}>
                <button onClick={bgChange}>{name}</button>
            </div>
        </>
    );
};

export default App;
