import React, { useState } from 'react';
import Clock from 'react-digital-clock';


const App = () => {
    const [count, incVal] = useState(0);

    const setNew =() =>{
    incVal(count +1);
    };

    const setDec = () => {
        if(count >0){
            incVal(count -1);
        }else{
            incVal(0);
            alert('Zero limit reached');
        }
    };
    return(
        <>
            <h1>{count}</h1>
            <Clock />

            <button onClick={setNew}>Increament</button>
            <button onClick={setDec}>Decreament</button>
           
        </>
    );
};

export default App;
