import React, { useState } from 'react';

const App = () => {

   const [inputList, setInputList] = useState();
   const itemEvent = (event) => {
      setInputList(event.target.value);
   };
return(
   <>
      <div>
         <div>
         <br />
         <h1>To Do List</h1>
         <br />
         <input type ='text' placeholder = 'Add a item' onChange={itemEvent}/>
         <button> +</button>
         <ol>
            <li>{inputList}</li>
         </ol>
         </div>
      </div>
   </>
);
};

export default App;
